        "dialogue": [
        {
            "speaker": "Emma",
            "line": "Entonces, Leo, ¿qué querías mostrarme aquí?"
        },
        {
            "speaker": "Leo",
            "line": "Paciencia, Emma. Es un poco... cómo debería decir... de otro nivel."
        },
        ]
    }
    ]
}

